import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { FaBook, FaUsers, FaChartLine, FaPlus, FaEdit, FaTrash, FaToggleOn, FaToggleOff } from 'react-icons/fa';
import './InstructorDashboard.css';

const InstructorDashboard = () => {
    const [courses, setCourses] = useState([]);
    const [stats, setStats] = useState({
        totalStudents: 0,
        totalCourses: 0,
        activeCourses: 0,
        totalRevenue: 0
    });
    const [showModal, setShowModal] = useState(false);
    const [editingCourse, setEditingCourse] = useState(null);
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        category: '',
        price: ''
    });

    useEffect(() => {
        fetchCourses();
        fetchStats();
    }, []);

    const fetchCourses = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:5000/api/instructor/courses', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            setCourses(response.data);
        } catch (error) {
            console.error('Error fetching courses:', error);
            toast.error('Failed to fetch courses');
        }
    };

    const fetchStats = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:5000/api/instructor/stats', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            setStats(response.data);
        } catch (error) {
            console.error('Error fetching stats:', error);
            toast.error('Failed to fetch statistics');
        }
    };

    const handleInputChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const courseData = {
                title: formData.title,
                description: formData.description,
                category: formData.category,
                price: parseFloat(formData.price),
                status: 'draft'
            };

            if (editingCourse) {
                await axios.put(
                    `http://localhost:5000/api/instructor/courses/${editingCourse._id}`,
                    courseData,
                    {
                        headers: {
                            'Authorization': `Bearer ${token}`,
                            'Content-Type': 'application/json'
                        }
                    }
                );
                toast.success('Course updated successfully');
            } else {
                await axios.post(
                    'http://localhost:5000/api/instructor/courses',
                    courseData,
                    {
                        headers: {
                            'Authorization': `Bearer ${token}`,
                            'Content-Type': 'application/json'
                        }
                    }
                );
                toast.success('Course created successfully');
            }
            setShowModal(false);
            setEditingCourse(null);
            setFormData({ title: '', description: '', category: '', price: '' });
            fetchCourses();
        } catch (error) {
            console.error('Error saving course:', error);
            toast.error(error.response?.data?.message || 'Failed to save course');
        }
    };

    const handleEdit = (course) => {
        setEditingCourse(course);
        setFormData({
            title: course.title,
            description: course.description,
            category: course.category._id,
            price: course.price
        });
        setShowModal(true);
    };

    const handleDelete = async (courseId) => {
        if (window.confirm('Are you sure you want to delete this course?')) {
            try {
                const token = localStorage.getItem('token');
                await axios.delete(`http://localhost:5000/api/instructor/courses/${courseId}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                });
                toast.success('Course deleted successfully');
                fetchCourses();
            } catch (error) {
                console.error('Error deleting course:', error);
                toast.error('Failed to delete course');
            }
        }
    };

    const handleToggleStatus = async (courseId) => {
        try {
            const token = localStorage.getItem('token');
            await axios.put(
                `http://localhost:5000/api/instructor/courses/${courseId}/toggle`,
                {},
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                }
            );
            toast.success('Course status updated');
            fetchCourses();
        } catch (error) {
            console.error('Error toggling course status:', error);
            toast.error('Failed to update course status');
        }
    };

    return (
        <div className="instructor-dashboard">
            <div className="dashboard-header">
                <h1>Instructor Dashboard</h1>
            </div>

            <div className="dashboard-grid">
                <div className="dashboard-card">
                    <div className="card-header">
                        <div className="card-icon">
                            <FaBook />
                        </div>
                        <h2>Course Statistics</h2>
                    </div>
                    <div className="card-content">
                        <div className="stats-grid">
                            <div className="stat-item">
                                <h3>Total Students</h3>
                                <p>{stats.totalStudents}</p>
                            </div>
                            <div className="stat-item">
                                <h3>Total Courses</h3>
                                <p>{stats.totalCourses}</p>
                            </div>
                            <div className="stat-item">
                                <h3>Active Courses</h3>
                                <p>{stats.activeCourses}</p>
                            </div>
                            <div className="stat-item">
                                <h3>Total Revenue</h3>
                                <p>${stats.totalRevenue}</p>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="dashboard-card">
                    <div className="card-header">
                        <div className="card-icon">
                            <FaUsers />
                        </div>
                        <h2>Course Management</h2>
                        <button className="add-course-btn" onClick={() => setShowModal(true)}>
                            <FaPlus /> Add Course
                        </button>
                    </div>
                    <div className="card-content">
                        <div className="courses-table-container">
                            <table className="courses-table">
                                <thead>
                                    <tr>
                                        <th>Title</th>
                                        <th>Category</th>
                                        <th>Students</th>
                                        <th>Status</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {courses.map(course => (
                                        <tr key={course._id}>
                                            <td>{course.title}</td>
                                            <td>{course.category.name}</td>
                                            <td>{course.enrolledStudents.length}</td>
                                            <td>
                                                <span className={`status-badge ${course.status}`}>
                                                    {course.status}
                                                </span>
                                            </td>
                                            <td>
                                                <div className="action-buttons">
                                                    <button
                                                        className="action-btn edit"
                                                        onClick={() => handleEdit(course)}
                                                    >
                                                        <FaEdit />
                                                    </button>
                                                    <button
                                                        className="action-btn delete"
                                                        onClick={() => handleDelete(course._id)}
                                                    >
                                                        <FaTrash />
                                                    </button>
                                                    <button
                                                        className="action-btn status"
                                                        onClick={() => handleToggleStatus(course._id)}
                                                    >
                                                        {course.status === 'active' ? <FaToggleOn /> : <FaToggleOff />}
                                                    </button>
                                                </div>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>

            {showModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h2>{editingCourse ? 'Edit Course' : 'Add New Course'}</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label htmlFor="title">Course Title</label>
                                <input
                                    type="text"
                                    id="title"
                                    name="title"
                                    value={formData.title}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="description">Description</label>
                                <textarea
                                    id="description"
                                    name="description"
                                    value={formData.description}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="category">Category</label>
                                <select
                                    id="category"
                                    name="category"
                                    value={formData.category}
                                    onChange={handleInputChange}
                                    required
                                >
                                    <option value="">Select a category</option>
                                    {/* Add categories dynamically */}
                                </select>
                            </div>
                            <div className="form-group">
                                <label htmlFor="price">Price ($)</label>
                                <input
                                    type="number"
                                    id="price"
                                    name="price"
                                    value={formData.price}
                                    onChange={handleInputChange}
                                    min="0"
                                    step="0.01"
                                    required
                                />
                            </div>
                            <div className="modal-actions">
                                <button type="submit" className="btn-primary">
                                    {editingCourse ? 'Update Course' : 'Create Course'}
                                </button>
                                <button
                                    type="button"
                                    className="btn-secondary"
                                    onClick={() => {
                                        setShowModal(false);
                                        setEditingCourse(null);
                                        setFormData({ title: '', description: '', category: '', price: '' });
                                    }}
                                >
                                    Cancel
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default InstructorDashboard;
